func TestNative_SkipOne_Error(t *testing.T) {
	Use()
	for _, s := range []string{
		"-", "+", "0.", "0. ", "+1", "0.0e ", "9e+", "0e-",
		"tru", "fals", "nul", "trux", "fals ",
		`"asdf`, `"\\\"`,
	} {
		p := 0
		q := skip_one(&s, &p, &types.StateMachine{}, uint64(0))
		assert.True(t, q < 0)
	}
}
